import './App.css';
import React,{useEffect,useState} from 'react'
function App() {
  const [users,setUser]=useState([])
  useEffect(()=>{
    fetch("https://reqres.in/api/users?page=1").then((result)=>{
      result.json().then((resp)=>{
        // console.warn(resp)
        setUser(resp)
      })
    })
  },[])
  console.warn(users)
  return (
    <div className="App">
      <h1>Get API Call </h1>
      <table border="1">
       <tbody>
       <tr>
          <td>ID</td>
          <td>email</td>
          <td>first_name</td>
          <td>last_name</td>
          <td>avatar</td>
        </tr>
        {
          users.map((item,i)=>
            <tr key={i}>
            <td>{item.userId}</td>
          <td>{item.email}</td>
          <td>{item.Fname}</td>
            <td>{item.lname}</td>
            <td>{item.avatar}</td>
          </tr>
          )
        }
       </tbody>
      </table>
    </div>
  );
}
export default App;
